import React from 'react';
// import logo from './logo.svg';
// import './App.css';

class App extends React.Component{
  constructor(){
    super()
    this.state = {
      mood : {'moods' : []}
    }
  }
  componentWillMount(){
    fetch('http://localhost:5000/get-moods').then(res => res.json()).then(res=> this.setState({mood:res}));
  }

  render(){  
    // fetch('http://localhost:5000/get-moods').then(res => res.json()).then(res=> mood = res);
    console.log(this.state.mood)
    return (
      <div>
        <h1>Moods</h1>
        <ul>
          <li>{this.state.mood['moods'][0]['mood']}</li>
        </ul>
        <a href="/add-mood"><button>Add a Mood</button></a>
      </div>
    )
  }
}

export default App;
