import React from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
} from 'react-router-dom';



class App extends React.Component{
  render(){  
return(
      <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/mood-list">Mood</Link>
            </li>
            <li>
              <Link to="/users">Users</Link>
            </li>
          </ul>
        </nav>

        <Switch>
          <Route path="/add-mood">
            <AddMood />
          </Route>
          <Route path="/mood-list">
            <MoodList />
          </Route>
          <Route path="/mood-list/:id">
            <ConfirmDeleteMood />
          </Route>
        </Switch>
      </div>
      </Router>
    )
  }
}

class MoodList extends React.Component{
  constructor(){
    super()
    this.state = {
      mood : {
        'moods':[{'mood':''}]
      }
    }
  }

  componentDidMount(){
    fetch('http://localhost:5000/get-moods').then(res => res.json()).then(res=> this.setState({mood:res}));
    console.log("added successfully")
  }

  render(){ 
    console.log(this.state.mood)
    const moodList = this.state.mood['moods'].map((mood)=>
      <li>
      {mood.mood} 
      <Link to="/edit-mood"><button>Edit</button></Link> 
      <Link to={`/confirm-delete-mood/${mood.id}`}><button>Delete</button></Link> 
      </li>
    )
    // for(let i=0; i<this.state.mood['moods'].length; i++){
    //   moodList.push(<MoodEntry id={this.state.mood['moods'][i]['id']} moodName={this.state.mood['moods'][i]['mood']} />)
    // }
    console.log(moodList)
    return (
      <div>
        <h1>Moods</h1>
        <ul>
          {moodList}
        </ul>
        <Link to="/add-mood"><button>Add a Mood</button></Link>
      </div>
    )
  }
}

