import React from 'react';
import axios from 'axios';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect,
  useParams,
} from 'react-router-dom';
import Session from 'react-session-api';
import ReactModal from 'react-modal';
import Profile from '../profile/Profile.js';
import MoodList from '../mood-list/MoodList.js';
import MedList from '../med-list/MedList.js';
import Register from '../register/Register.js';
import LogIn from '../login/LogIn.js';
import Logout from '../logout/Logout.js';
import Navbar from '../navbar/Navbar.js';
import Home from '../home/Home.js';
import Splash from '../splash/Splash.js';


// Styling
import 'bootstrap/dist/css/bootstrap.min.css'
import { Container, Row, Col, Button } from 'react-bootstrap';
import '../../styles/css/main.css';


ReactModal.setAppElement('#root')

class App extends React.Component{
  constructor() {
    super();
    this.state = {
      sessionId: '',
      // sessionEmail: ''
      showHome: false,
      showSplash: true
    }
  }

  componentDidMount() {
    const setCallback = (data) => {
      this.setState({
        sessionId: data['id']
        // sessionEmail: data['email']
      })
    }
    Session.onSet(setCallback)
  }

  render(){
    const data = Session.get('user')
    // let dataLi;
    // if(data) {
    //   dataLi = <li>{data['email']}</li>
    // }

    if(this.state.sessionId){
      this.setState({
        showSplash: false,
        showHome : true
      })
    }

    let display;
    if(showSplash){
      splash = <Splash />
    }

    if(showHome){
      splash = <Home />
    }


    return(
      <Router>
      <div className="page-constraint">

          {/* <Button className="btn-primary-solid" onClick={this.Register}>Register</Button>
          <Button className="btn-primary-solid-alt" onClick={this.Login}>Login</Button> */}

          {/* Replace these buttons with above, paths need to be fixed */}

          {/* <Button className="btn-primary-solid">
            <Link to="/register">Register</Link>
          </Button>
          <Button className="btn-primary-solid"> 
            <Link to="/login">Login</Link>
          </Button>
          <Button className="btn-primary-solid"> 
            <Link to="/logout">Logout</Link>
          </Button>
          */}




        <Switch>
          <Route path="/splash">
            <Splash />
          </Route>
          <Route path="/">
            <Home />
          </Route>
          <Route path="/mood-list">
            <MoodList />
          </Route>
          <Route path="/med-list">
            <MedList />
          </Route>
          <Route path="/register">
            <Register />
          </Route>
          <Route path="/login">
            <LogIn />
          </Route>
          <Route path="/logout">
            <Logout />
          </Route>
          <Route path="/profile">
            <Profile />
          </Route>
        </Switch>

        <Navbar fixed="bottom"/>
      </div>
      </Router>
    )
  }
}

export default App;
