import React from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from 'react-router-dom';

class MoodEntry extends React.Component{
  render(){
    return <li>{this.props.moodName}</li>
  }
}

class App extends React.Component{
  constructor(){
    super()
    this.state = {
      mood : {
        'moods':[{'mood':""}]
      }
    }
  }
  componentWillMount(){
    fetch('http://localhost:5000/get-moods').then(res => res.json()).then(res=> this.setState({mood:res}));
  }

  render(){  
    // fetch('http://localhost:5000/get-moods').then(res => res.json()).then(res=> mood = res);
    console.log(this.state.mood)
    const moodList = []
    for(let i=0; i<this.state.mood['moods'].length; i++){
      moodList.push(<MoodEntry moodName={this.state.mood['moods'][i]['mood']} />)
    }
    console.log(moodList)
    return (
      <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/about">About</Link>
            </li>
            <li>
              <Link to="/users">Users</Link>
            </li>
          </ul>
        </nav>

        <h1>Moods</h1>
        <ul>
          {moodList}
        </ul>
        <a Link to="/add-mood"><button>Add a Mood</button></a>

        <Switch>
          <Route path="/add-mood">
            <AddMood />
          </Route>
        </Switch>
      </div>
      </Router>
    )
  }
}

class AddMood extends React.Component{
  render(){
    console.log("add")

    return(
      <a href="/add-mood"><button>Add a Mood</button></a>
      )
  }
}

export default App;
